mapper.insert:
SELECT NVL(MAX(RESUME_NO), 0) + 1 FROM RESUME
INSERT INTO RESUME (RESUME_NO, RESUME_NAME, RESUME_BIRTH, RESUME_EMAIL, RESUME_PHONE, RESUME_ADDRESS, RESUME_SCHOOL, RESUME_AREA, RESUME_START, RESUME_END, RESUME_TITLE, ID, RESUME_MAJOR
,IMAGE
) VALUES (#{resume_no}, #{resume_name}, #{resume_birth}, #{resume_email}, #{resume_phone}, #{resume_address}, #{resume_school}, #{resume_area}, #{resume_start}, #{resume_end}, #{resume_title}, #{id}, #{resume_major}
,#{image}
)

mapper.update:
UPDATE RESUME
SET MENTORID=#{mentorId}
WHERE RESUME_NO=#{resume_no}
