mapper.delete:
DELETE FROM SHOPPING
WHERE ID = #{id}
AND MENTORING_NUMBER = #{mentoring_number}

mapper.select:
SELECT COUNT(*) FROM SHOPPING
WHERE ID = #{id}
AND MENTORING_NUMBER = #{mentoring_number}

SELECT * FROM SHOPPING
WHERE ID = #{id}
AND MENTORING_NUMBER = #{mentoring_number}

SELECT COUNT(*) FROM SHOPPING
WHERE ID = #{id}
AND S_STATE = '구매완료'

SELECT * FROM SHOPPING
WHERE ID = #{id}
AND MENTORING_NUMBER = #{mentoring_number}
AND S_STATE = '구매완료'
